$(document).ready(function(){getGlobalVars();loadNavAndFoot()});function isLogged(){$.ajax({url:"",type:"get",async:false,dataType:"json",success:function(a){if(a==="0"){logout()}},error:function(a){alert("----ajax请求验证是否存在登录用户执行出错！错误信息如下：----\n"+a.responseText)}})}function getGlobalVars(){let strArr=window.sessionStorage.getItem("loggedAdminInfo").split("_");loggedAdminInfo={adminName:window.atob(strArr[0]),password:window.atob(strArr[1]),email:window.atob(strArr[2])};let cno=window.sessionStorage.getItem("cno");if(cno!==null){currentComponent=cno}}function loadNavAndFoot(){loadSingleComponent(systemComponents.c0,"#index-navbar-container");loadSingleComponent(systemComponents.c1,"#index-foot-container");loadBody()}function loadBody(){let tabs=$("#index-navbar ul > li > a");$.each(tabs,function(key,value){if($(value).text().replace(/\s/g,"")===eval("systemComponents.c"+currentComponent+".cname")){$(value).trigger("click")}})};