function index_body_addAdmin_init(){$("#addAdminResetButton").click(function(){$("#addAdmin-form").data("bootstrapValidator").resetForm(true);$("#addAdminButton").button("reset")});$("#addAdmin-form").bootstrapValidator({message:"*输入不合法",feedbackIcons:{valid:"glyphicon glyphicon-ok",invalid:"glyphicon glyphicon-remove",validating:"glyphicon glyphicon-refresh"},fields:{newUserName:{message:"*用户名不合法",validators:{notEmpty:{message:"*用户名不能为空"},stringLength:{min:6,max:10,message:"*用户名长度为6-10（包含）"},regexp:{regexp:/^[a-zA-Z0-9_.]+$/,message:"*用户名只能包含字母、数字、下划线和点"},remote:{url:"",type:"post",message:"该用户名已存在"}}},newUserPassword:{message:"*密码不合法",validators:{notEmpty:{message:"*密码不能为空"},stringLength:{min:6,max:6,message:"*密码必须为6位"},different:{field:"newUserName",message:"*密码不能和用户名相同"}}},newUserPasswordConfirm:{message:"*密码确认失败",validators:{notEmpty:{message:"*请再次输入密码"},identical:{field:"newUserPassword",message:"*两次输入的密码不相同"}}},newUserEmail:{message:"*邮箱不合法",validators:{notEmpty:{message:"*邮箱不能为空"},emailAddress:{message:"*请输入合法的邮箱地址"}}}}}).on("success.form.bv",function(a){a.preventDefault();$("#addAdminButton").button("loading");let newUserName=$("input[name='newUserName']").val();let newUserPassword=$("input[name='newUserPassword']").val();let newUserEmail=$("input[name='newUserEmail']").val();let jsonStr=JSON.stringify({newUserName:newUserName,newUserPassword:newUserPassword,newUserEmail:newUserEmail});$.ajax({url:"addAdmin",type:"post",data:jsonStr,contentType:"application/json;charset=utf-8",async:false,dataType:"json",success:function(b){if(b==="0"){myAlert("啊欧，添加管理员失败了(x_x)!")}else{myAlert("管理员添加成功(∩_∩)!")}$("#addAdminButton").button("reset");$("#addAdmin-form").data("bootstrapValidator").resetForm(true)},error:function(b){alert("----ajax请求添加管理员执行出错！错误信息如下：----\n"+b.responseText)}})})};