function index_body_allAdmin_init(){$.ajax({url:"getAllAdmins",type:"get",async:true,dataType:"json",success:function(a){if(a==="0"){alert("获取管理员列表失败!")}else{let html="";$.each(a,function(b,c){html+="<tr><th scope='row'>"+c.adminId+"</th><td>"+c.adminName+"</td><td>"+c.password+"</td><td>"+c.email+"</td><td><a href='javascript:void(0)' onclick='deleteAdmin(this)'>删除</a></td></tr>"});$("#allAdmin-table tbody tr").remove();$("#allAdmin-table tbody").append(html)}},error:function(a){alert("----ajax请求加载显示管理员列表出错！错误信息如下：----\n"+a.responseText)}})}function deleteAdmin(a){let tds=$(a).parent().parent().children();let adminName=$(tds[1]).text();let password=$(tds[2]).text();let jsonStr=JSON.stringify({adminName:adminName,password:password});$.ajax({url:"deleteAdmin",type:"post",data:jsonStr,contentType:"application/json;charset=utf-8",async:true,dataType:"json",success:function(b){if(b==="0"){myAlert("删除失败!")}else{myAlert("删除成功!");index_body_allAdmin_init()}},error:function(b){alert("----ajax请求加删除管理员出错！错误信息如下：----\n"+b.responseText)}})};